mapper.select:
select  j.j_num, c.cam_name, j.is_jjim from jjim as j join camping as c
on j.cam_code = c.cam_code
where j.id = #{id};

select c.cp_name, my.use_coupon, c.cp_start_date , c.cp_end_date from my_coupon my join coupon c
on my.cp_code = c.cp_code
where my.id = #{id}

SELECT id,f_code,pw,name,nick,birth,phone,email
from user
where id = #{id}
